import { useState } from "react";
import clsx from 'clsx';

interface Option {
    id: string;
    name: string;
}

interface OptionListProps {
    options: Option[];
    open: boolean;
}

interface Menu {
    name: string;
    options: Option[];
}

interface MenuProps {
    menu: Menu;
}
  
const OptionList: React.FC<OptionListProps> = ({ options, open }) => {
    return (
        <ul
            className={clsx(
                'flex flex-col list-none font-normal text-xs pl-3 transition-all duration-500 overflow-hidden',
                {
                'h-0 opacity-0 absolute top-9 w-full': !open,
                'h-auto opacity-100 relative': open,
                }
            )}
            >
            {options.map(option => (
                <li key={option.id} className="cursor-pointer">
                {option.name}
                </li>
            ))}
        </ul>
    );
};

const menuOptions: Menu[] = [
    {
      name: 'Principal',
      options: [
        { id: '21331232', name: 'Mapeamento' },
        { id: '21231232', name: 'Trteinamentos IA' },
        { id: '21431232', name: 'RPA' },
        { id: '21412323', name: 'Dashboard' },
        { id: '21512323', name: 'Cadastros' },
      ],
    },
];
  
const MenuComponent: React.FC<MenuProps> = ({ menu }) => {
    const [open, setOpen] = useState(false);

    return (
        <li className="flex flex-col text-white text-sm relative" onClick={() => setOpen(!open)}>
            <span className="cursor-pointer font-semibold">{menu.name}</span>
            <OptionList options={menu.options} open={open} />
        </li>
    );
};

export const MenuOpen: React.FC = () => {
    return (
        <>
            {menuOptions.map(menu => (
                <MenuComponent key={menu.name} menu={menu} />
            ))}
        </>
    );
};
