import React, { useCallback } from 'react';
import ReactFlow, {
  ReactFlowProvider,
  addEdge,
  MiniMap,
  Background,
  Controls,
  Connection,
  Edge,
  Node,
  applyEdgeChanges,
  applyNodeChanges,
  ConnectionMode,
  useEdgesState,
} from 'reactflow';
import 'reactflow/dist/style.css';
import { zinc } from 'tailwindcss/colors';
import { Square } from './FlowJam/Square';

const NODE_TYPES = {
   square: Square, 
}

const INITIAL_NODES = [
    {
        id: crypto.randomUUID(),
        type: "square",
        position: {
            x: 200,
            y: 400,
        },
        data: {},
    },
    {
        id: crypto.randomUUID(),
        type: "square",
        position: {
            x: 1000,
            y: 400,
        },
        data: {},
    },
] satisfies Node[]

const OldFluxograma: React.FC = () => {
    const [edges, setEdges, onEdgesChange] = useEdgesState([]);

    const onConnect = useCallback((connection: Connection) => {
        return setEdges(edges => addEdge(connection, edges))
    }, []);

    return (
        <div className='w-screen h-screen'>
            <ReactFlow
                nodeTypes={NODE_TYPES}
                nodes={INITIAL_NODES}
                edges={edges}
                onEdgesChange={onEdgesChange}
                onConnect={onConnect}
                ConnectionMode={ConnectionMode.Loose}
            >
                <Background 
                    gap={12}
                    size={12}
                    color={zinc[200]}
                />
                <Controls />
            </ReactFlow>
        </div>
    )
};

export default OldFluxograma;
