import React, { useState } from 'react';
import ReactFlow, { MiniMap, Controls, Background, Node, Edge } from 'reactflow';
import 'reactflow/dist/style.css';

const initialElements = [
  { id: '1', type: 'input', data: { label: 'Input Node' }, position: { x: 100, y: 100 } },
  { id: '2', type: 'output', data: { label: 'Output Node' }, position: { x: 300, y: 100 } },
  { id: 'e1-2', source: '1', target: '2', animated: true },
];

const Fluxograma: React.FC = () => {
  const [elements, setElements] = useState(initialElements);

  const addNode = () => {
    const newNode = {
      id: (elements.length + 1).toString(),
      type: 'default',
      data: { label: `Node ${elements.length + 1}` },
      position: { x: Math.random() * 500, y: Math.random() * 500 },
    };

    setElements(prevElements => [...prevElements, newNode]);
  };

  return (
    <div style={{ width: '100%', height: '100vh' }}>
      <ReactFlow elements={elements as any} style={{ width: '100%', height: '100%' }}>
        <MiniMap />
        <Controls />
        <Background />
      </ReactFlow>
      <button onClick={addNode}>Adicionar NÃ³</button>
    </div>
  );
};

export default Fluxograma;
