import React, { useState, useEffect } from 'react';
import { Process, ProcessStepType } from '../types';
import ProcessStepComponent from './ProcessStep';
import { getProcesses, createProcess, updateProcess, deleteProcess } from '../services/api';

const ProcessFlow: React.FC = () => {
  const [processes, setProcesses] = useState<Process[]>([]);
  const [newProcessName, setNewProcessName] = useState('');
  const [newStepName, setNewStepName] = useState('');

  useEffect(() => {
    const fetchProcesses = async () => {
      const data = await getProcesses();
      setProcesses(data);
    };
    fetchProcesses();
  }, []);

  const handleAddStep = (processId: string) => {
    const updatedProcesses = processes.map((process) => {
      if (process.id === processId) {
        const newStep: ProcessStepType = { id: Date.now().toString(), name: newStepName };
        return { ...process, steps: [...process.steps, newStep] };
      }
      return process;
    });
    setProcesses(updatedProcesses);
  };

  const handleRemoveStep = (processId: string, stepId: string) => {
    const updatedProcesses = processes.map((process) => {
      if (process.id === processId) {
        return { ...process, steps: process.steps.filter((step) => step.id !== stepId) };
      }
      return process;
    });
    setProcesses(updatedProcesses);
  };

  const handleCreateProcess = async () => {
    const newProcess: Process = { id: Date.now().toString(), name: newProcessName, steps: [] };
    const createdProcess = await createProcess(newProcess);
    setProcesses([...processes, createdProcess]);
  };

  return (
    <div>
      <input
        type="text"
        value={newProcessName}
        onChange={(e) => setNewProcessName(e.target.value)}
        placeholder="New process name"
      />
      <button onClick={handleCreateProcess}>Create Process</button>

    </div>
  );
};

export default ProcessFlow;
