import clsx from 'clsx';
import React, { useState } from 'react';

interface Option {
  id: string;
  name: string;
}

interface Menu {
  name: string;
  options: Option[];
}

const menuOptions: Menu[] = [
  {
    name: 'Process',
    options: [
      { id: '21331232', name: 'Compras' },
      { id: '21231232', name: 'Financeiro' },
      { id: '21431232', name: 'Marketing' },
      { id: '21412323', name: 'Comercial' },
      { id: '21512323', name: 'Jurídico' },
    ],
  },
];

interface OptionListProps {
  options: Option[];
  open: boolean;
}

const OptionList: React.FC<OptionListProps> = ({ options, open }) => {
  return (
    <ul 
        style={open ? {display: 'flex'} : {display: 'none'}}
        className={'flex flex-col list-none font-normal text-xs pl-3'}
    >
      {options.map(option => (
        <li key={option.id} className="cursor-pointer">
          {option.name}
        </li>
      ))}
    </ul>
  );
};

interface MenuProps {
  menu: Menu;
}

const MenuComponent: React.FC<MenuProps> = ({ menu }) => {
    const [ open, setOpen ] = useState(false);

    return (
        <li className="flex flex-col text-white text-sm" onClick={() => setOpen(!open)}>
            <span className="cursor-pointer font-semibold">{menu.name}</span>
            <OptionList options={menu.options} open={open} />
        </li>
    );
};

const MenuOptions: React.FC = () => {
  return (
    <>
      {menuOptions.map(menu => (
        <MenuComponent key={menu.name} menu={menu} />
      ))}
    </>
  );
};

export const MenuDash: React.FC = () => {
  return (
    <div className="flex flex-col bg-blue-950 p-4">
      <ul className="flex flex-col list-none">
        <MenuOptions />
      </ul>
    </div>
  );
};
