import { Handle, Node, NodeProps, Position, useNodeId, useStore } from 'reactflow';
import { useEffect, useRef, useState } from 'react'; // Importe useEffect e useRef

export function Square({ selected }: NodeProps) {
  const nodeRef = useRef(null); // Crie uma referência para o nó

  useEffect(() => {
    const node = nodeRef.current; // Obtenha o elemento DOM do nó
    const observer = new ResizeObserver(() => {
      // Faça alterações somente se o nó estiver selecionado
      if (selected) {
        // Realize as operações necessárias em resposta ao redimensionamento
        console.log('Node resized');
      }
    });

    if (node) {
      observer.observe(node); // Comece a observar o nó para mudanças de tamanho
    }

    return () => {
      if (node) {
        observer.unobserve(node); // Pare de observar o nó ao desmontar o componente
      }
    };
  }, [selected]); // Atualize o efeito apenas quando a propriedade "selected" mudar

  return (
    <div ref={nodeRef} className="bg-violet-500 rounded min-w-[200px] min-h-[200px] w-full h-full group">
      <Handle 
        id="top"
        type="source" 
        position={Position.Top} 
        className="-top-5 bg-transparent w-3 h-3 border-2 border-blue-400" 
      />
      <Handle 
        id="bottom"
        type="source" 
        position={Position.Bottom} 
        className="-bottom-5 bg-transparent w-3 h-3 border-2 border-blue-400" 
      />
      <Handle 
        id="left"
        type="source" 
        position={Position.Left} 
        className="-left-5 bg-transparent w-3 h-3 border-2 border-blue-400" 
      />
      <Handle 
        id="right"
        type="source" 
        position={Position.Right} 
        className="-right-5 bg-transparent w-3 h-3 border-2 border-blue-400" 
      />
    </div>
  )
}
