import { MenuOpen } from "./MenuOpen";
import { UserInfo } from "./UserInfo";
import clsx from "clsx";

type TMenu = {
    open: boolean, 
    setOpen: any
}

export const Menu = ({ open, setOpen }: TMenu) => {

    return (
        <div className={clsx(
            "flex flex-col bg-blue-950 px-4 py-8 md:p-8 gap-8 max-[768px]:fixed top-0 bottom-0 z-[9] transition-transform",
            {
                'translate-x-0' : open,
                'translate-x-[-200%]' : !open,
            }
            )}>
            <UserInfo />
            <MenuOpen />

            <div className='flex md:hidden items-center'>
                <button className='text-white border border-white rounded-full px-2 py-1  font-normal text-sm hover:underline'>
                    Sing Out
                </button>
            </div>

            <button onClick={() => setOpen(false)}>
                fechar
            </button>
        </div>
    );
};
